macro.dbt_utils.default__get_url_path.macro_sql:
{% macro default__get_url_path(field) -%}

    {%- set stripped_url =
        replace(
            replace(field, "'http://'", "''"), "'https://'", "''")
    -%}

    {%- set first_slash_pos -%}
        coalesce(
            nullif({{ position("'/'", stripped_url) }}, 0),
            {{ position("'?'", stripped_url) }} - 1
            )
    {%- endset -%}

    {%- set parsed_path =
        split_part(
            right(
                stripped_url,
                length(stripped_url) ~ "-" ~ first_slash_pos
                ),
            "'?'", 1
            )
    -%}

    {{ safe_cast(
        parsed_path,
        type_string()
    )}}

{%- endmacro %}
